sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {



    var startDateTime = new GlideDateTime(current.starttime);
    var endDateTime = new GlideDateTime(current.endtime);

      // Set start time to beginning of the day
//     startDateTime.setHoursOfDay(0);
//     startDateTime.setMinuteOfDay(0);
//     startDateTime.setSecondOfDay(0);
//     startDateTime.setMillisecondOfDay(0);


//     // Set end time to end of the day
//     endDateTime.setHourOfDay(23);
//     endDateTime.setMinuteOfDay(59);
//     endDateTime.setSecondOfDay(59);
//     endDateTime.setMillisecondOfDay(999);

    var gr = new GlideRecord('x_980836_car_bookings');

    gr.addQuery('userid', current.userid);
    gr.addQuery('starttime', '<=', startDateTime );
    gr.addQuery('endtime', '>=', endDateTime);
    gr.query();

    if (gr.hasNext()) {
        gs.addErrorMessage('You have already booked a parking spot for this day.');
        current.setAbortAction(true);

    }


})(current, previous);
