sys_script_client.script:
function onLoad() {
    populateDates();
}

function populateDates() {
    var choiceField = g_form.getControl('date');

    var date = new Date();
    var dateList = [];

    // Generate dates for the next 8 days including today
    for (var i = 0; i < 9; i++) {
        var formattedDate = date.toLocaleDateString('en-GB', {
            weekday: 'short',
            month: 'short',
            day: 'numeric'
        });
        if (i == 0) {
            formattedDate = "Today";
        }

        dateList.push(formattedDate);
        date.setDate(date.getDate() + 1);
    }

    // Clear existing choices
    choiceField.options.length = 0;

    // Add new choices
    for (var i = 0; i < dateList.length; i++) {
        var dateOption = new Option(dateList[i], dateList[i]);
        choiceField.options.add(dateOption);
    }
}

populateDates();
