sys_script_client.script:
function onLoad() {
    populateTimes();
}

function populateTimes() {
    var choiceField = g_form.getControl('availability');
    var array = [];

	var date = new Date();
  date.setHours(0, 0, 0, 0); // Set the time to 12am

  // Generate time blocks in 3-hour increments from 12am to 12am
  for (var i = 0; i < 8; i++) {
    var startTime = date.toLocaleTimeString('en-US', {hour: '2-digit', minute: '2-digit'});
    date.setHours(date.getHours() + 3); // Increment the time by 3 hours
    var endTime = date.toLocaleTimeString('en-US', {hour: '2-digit', minute: '2-digit'});

    var timeBlock = startTime + ' - ' + endTime;
    array.push(timeBlock);
  }

    //     // Clear existing choices
    choiceField.options.length = 0;

	// add new choice list
	for (var i = 0; i < array.length; i++) {
		var choiceOption = new Option(array[i], array[i]);
		choiceField.options.add(choiceOption);
	}
}

populateTimes();
