sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

// Client Script
function onChangeStartTime() {
  var startField = g_form.getControl('starttime');
  var endField = g_form.getControl('endtime');

  // Clear end time field
  endField.options.length = 0;

  // Get selected start time
  var selectedStart = startField.value;

  if (selectedStart) {
    // Get time slots from server-side script
    var timeSlotGenerator = new TimeSlotGenerator();
    var ga = new GlideAjax('TimeSlotGenerator');
    ga.addParam('sysparm_name', 'generateTimeSlots');
    var response = ga.getXMLWait();
    var timeSlots = JSON.parse(response.responseText).result;

    // Filter time slots based on selected start time and add to end time field
    var selectedIndex = timeSlots.indexOf(selectedStart);
    var filteredSlots = timeSlots.slice(selectedIndex, selectedIndex + 12);
    filteredSlots.forEach(function(slot) {
      var option = new Option(slot, slot);
      endField.options.add(option);
    });
  }
}

	// Client Script (continued)
var startField = g_form.getControl('starttime');
startField.observe('change', onChangeStartTime);


}
