sys_script_client.script:
function onLoad() {
    populateTimes();

}

function populateTimes() {
    var startChoiceField = g_form.getControl('starttime');
    var endChoiceField = g_form.getControl('endtime');

    // Clear existing choices
    startChoiceField.options.length = 0;
    endChoiceField.options.length = 0;

    // Add "None" option to start time choice fields
    var noneOptionStart = new Option('--None--', '');
    startChoiceField.options.add(noneOptionStart);

    // Add "None" option to end time choice fields
	var noneOptionEnd = new Option('--None--', '');
    endChoiceField.options.add(noneOptionEnd);

    // Generate choices for hours and minutes in 15 minute increments
    var hours = ['12', '01', '02', '03', '04', '05', '06', '07', '08', '09', '10', '11'];
    var minutes = ['00', '15', '30', '45'];
    var ampm = ['AM', 'PM'];


    for (var k = 0; k < ampm.length; k++) {
        for (var i = 0; i < hours.length; i++) {
            for (var j = 0; j < minutes.length; j++) {
                var formattedTime = hours[i] + ':' + minutes[j] + ' ' + ampm[k];
                var timeOption = new Option(formattedTime, formattedTime);
                startChoiceField.options.add(timeOption);
                endChoiceField.options.add(timeOption.cloneNode(true));
            }
        }
    }
}

// Call the function when the form loads
populateTimes();
