sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    // Get the start time value and convert it to a Date object
    var startTime = g_form.getValue('start');
    var startDate = new Date("January 01, 1970 00:00:00");
    startDate.setMinutes(startTime);

    // Add 3 hours to the start time to get the end time
    var endDate = new Date(startDate.getTime() + (0.25 * 60 * 60 * 1000));
    var hours = endDate.getHours();
    var minutes = endDate.getMinutes();
    var ampm = hours >= 12 ? 'PM' : 'AM';
    hours = (hours % 12) || 12;
    var formattedEndTime = hours.toString().padStart(2, '0') + ':' + minutes.toString().padStart(2, '0') + ' ' + ampm;

    // Set the end time value
    g_form.setValue('endtime', formattedEndTime);
}
