sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

    var start_time = g_form.getValue('start_time');
	var end_time = g_form.getValue('end_time');
	var oneWeekFromNow = new Date();
	var end = new Date(end_time);

		// Booking more than a week in advance is not allowed
		oneWeekFromNow.setDate(oneWeekFromNow.getDate() + 7);
		if(end > oneWeekFromNow) {
		g_form.addErrorMessage('You can only book one week in advanced!');
		g_form.setValue('start_time', oldValue);
		return;
	}

	// End time booking can only be max 3 hours from the start time
	var start = new Date(start_time);
	var diffHours = (end.getTime() - start.getTime());
	if(diffHours > 10800000) {
		g_form.addErrorMessage('You can only book for 3 hours');
		g_form.setValue('end_time', '');
	}


	// End can not be in the past
	var now = new Date();
	if(end < now) {
		g_form.addErrorMessage('End time cannot be in the past.');
		g_form.setValue('end_time', oldValue);
		return;
	}
	// End time must be after the start time
		if(end < start) {
			g_form.addErrorMessage('End time can not be before the start time');
		}








// 	var userid = g_form.getValue(('userid'));
// 	var today = new GlideDateTime().getDisplayValue();
// 	var booking = new GlideRecord('x_980836_car_bookings');
// 	booking.addQuery('userid', userid);
// 	booking.addQuery('start_time', '<=', today.getLocalDate());
// 	booking.addQuery('end_time', '>=', today.getLocalDate());
// 	booking.query();

// 	if(booking.hasNext()) {
// 		alert('You have already booked a parking spot for this time period.');
// 		g_form.setValue('start_time', '');
// 		g_form.setValue('end_time', '');

// 	}

	}
