sys_script_fix.script:
(function (minutes) {
	var createChoice = createChoiceFor('x_980836_car_bookings', 'start'),
		choiceValueExist = choiceValueExistFor('x_980836_car_bookings', 'start');

	while (minutes < 1440) {
		if (!choiceValueExist(minutes))
			createChoice(minutes);

		minutes += 15;
	}

	function createChoiceFor (name, element) {
		var secondsInMinutes = 60;
		var millisecondsInSeconds = 1000;

		return function (value) {
			var milliseconds = value * secondsInMinutes * millisecondsInSeconds;
			var gt = new GlideTime(milliseconds);

			return new global.GlideQuery('sys_choice')
				.insert({
					'name': name,
					'element': element,
					'value': '' + value,
					'language': 'en',
					'label': gt.getByFormat('hh:mm aaa'),
					'inactive': false,
					'sequence': value,
				});
		};
	}

	function choiceValueExistFor (name, element) {
		return function (value) {
			return !new global.GlideQuery('sys_choice')
				.where('name', name)
				.where('element', element)
				.where('value', '' + value)
				.where('language', 'en')
				.selectOne([])
				.isEmpty();
		};
	}
})(0);
